import React, { useEffect } from "react";
import "./App.css";
import { NYTIMESSERVICE } from "./services/nyservice";
import Spinner from './components/Loader';
import NyListItem from './components/NyListItem';
import InputBase from '@material-ui/core/InputBase';
import { fade, makeStyles } from '@material-ui/core/styles';

const App = () => {
  const [getMostpopularArticles, setMostpopularArticles] = React.useState(null);
  const useStyles = makeStyles(theme => ({
    root: {
      flexGrow: 1,
    },
    menuButton: {
      marginRight: theme.spacing(2),
    },
    title: {
      flexGrow: 1,
      display: 'none',
      [theme.breakpoints.up('sm')]: {
        display: 'block',
      },
    },
    search: {
      position: 'relative',
      borderRadius: theme.shape.borderRadius,
      backgroundColor: fade(theme.palette.common.white, 0.15),
      '&:hover': {
        backgroundColor: fade(theme.palette.common.white, 0.25),
      },
      marginLeft: 0,
      width: '100%',
      [theme.breakpoints.up('sm')]: {
        marginLeft: theme.spacing(1),
        width: 'auto',
      },
    },
    searchIcon: {
      width: theme.spacing(7),
      height: '100%',
      position: 'absolute',
      pointerEvents: 'none',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
    },
    inputRoot: {
      color: 'inherit',
    },
    inputInput: {
      padding: theme.spacing(1, 1, 1, 7),
      transition: theme.transitions.create('width'),
      width: '100%',
      [theme.breakpoints.up('sm')]: {
        width: 120,
        '&:focus': {
          width: 200,
        },
      },
    },
  }));

  useEffect(() => {
    async function fetchData() {
      // You can await here
      try {
        const res = await NYTIMESSERVICE.getMostpopularArticles(1);
        setMostpopularArticles(res.results);
        console.log(res);
      } catch (err) {}
    }
    fetchData();
  }, []);
  return (
    <div className="App">
      <div className="head-bar">
        <nav role="navigation">
          <div className="navrolemain">
            <div className="navrole1">
              <div id="menuToggle">
                <input type="checkbox" />
                <span></span>
                <span></span>
                <span></span>
              </div>
              <div className="head-text">NY Times Most Popular Article</div>
              <div className={classes.search}>
            <div className={classes.searchIcon}>
              <SearchIcon />
            </div>
            <InputBase
              placeholder="Search…"
              classes={{
                root: classes.inputRoot,
                input: classes.inputInput,
              }}
              inputProps={{ 'aria-label': 'search' }}
            />
          </div>
            </div>
            <div className="navrole2">
              <div className="vert-menu"></div>
            </div>
          </div>
        </nav>
      </div>
      <div className="result-section">
        {getMostpopularArticles && (
          <NyListItem listArray={getMostpopularArticles} />
        )}
      </div>
      {getMostpopularArticles === null && <Spinner></Spinner>}
    </div>
  );
};

export default App;
